					$affectedRows += $this->aUsrRelatedByAuthorid->save($con);
				}
				$this->setUsrRelatedByAuthorid($this->aUsrRelatedByAuthorid);
			}

			if ($this->aStat !== null) {
				if ($this->aStat->isModified() || $this->aStat->isNew()) {
					$affectedRows += $this->aStat->save($con);
				}
				$this->setStat($this->aStat);
			}

			if ($this->aUrgency !== null) {
				if ($this->aUrgency->isModified() || $this->aUrgency->isNew()) {
					$affectedRows += $this->aUrgency->save($con);
				}
				$this->setUrgency($this->aUrgency);
			}

			if ($this->isNew() ) {
				$this->modifiedColumns[] = TaskPeer::ID;
			}

			// If this object has been modified, then save it to the database.
			if ($this->isModified()) {
				if ($this->isNew()) {
					$pk = TaskPeer::doInsert($this, $con);
					$affectedRows += 1; // we are assuming that there is only 1 row per doInsert() which
										 // should always be true here (even though technically
										 // BasePeer::doInsert() can insert multiple rows).

					$this->setId($pk);  //[IMV] update autoincrement primary key

					$this->setNew(false);
				} else {
					$affectedRows += TaskPeer::doUpdate($this, $con);
				}

				$this->resetModified(); // [HL] After being saved an object is no longer 'modified'
