	public function getTaskComments($criteria = null, PropelPDO $con = null)
	{
		if ($criteria === null) {
			$criteria = new Criteria(TaskPeer::DATABASE_NAME);
		}
		elseif ($criteria instanceof Criteria)
		{
			$criteria = clone $criteria;
		}

		if ($this->collTaskComments === null) {
			if ($this->isNew()) {
			   $this->collTaskComments = array();
			} else {

				$criteria->add(TaskCommentPeer::TASKID, $this->id);

				TaskCommentPeer::addSelectColumns($criteria);
				$this->collTaskComments = TaskCommentPeer::doSelect($criteria, $con);
			}
		} else {
			// criteria has no effect for a new object
			if (!$this->isNew()) {
				// the following code is to determine if a new query is
				// called for.  If the criteria is the same as the last
				// one, just return the collection.


				$criteria->add(TaskCommentPeer::TASKID, $this->id);

				TaskCommentPeer::addSelectColumns($criteria);
				if (!isset($this->lastTaskCommentCriteria) || !$this->lastTaskCommentCriteria->equals($criteria)) {
					$this->collTaskComments = TaskCommentPeer::doSelect($criteria, $con);
				}
			}
		}
		$this->lastTaskCommentCriteria = $criteria;
		return $this->collTaskComments;
	}

	/**
