	 * @return     int Count of related TaskComment objects.
	 * @throws     PropelException
	 */
	public function countTaskComments(Criteria $criteria = null, $distinct = false, PropelPDO $con = null)
	{
		if ($criteria === null) {
			$criteria = new Criteria(TaskPeer::DATABASE_NAME);
		} else {
			$criteria = clone $criteria;
		}

		if ($distinct) {
			$criteria->setDistinct();
		}

		$count = null;

		if ($this->collTaskComments === null) {
			if ($this->isNew()) {
				$count = 0;
			} else {

				$criteria->add(TaskCommentPeer::TASKID, $this->id);

				$count = TaskCommentPeer::doCount($criteria, false, $con);
			}
		} else {
			// criteria has no effect for a new object
			if (!$this->isNew()) {
				// the following code is to determine if a new query is
				// called for.  If the criteria is the same as the last
				// one, just return count of the collection.


				$criteria->add(TaskCommentPeer::TASKID, $this->id);

				if (!isset($this->lastTaskCommentCriteria) || !$this->lastTaskCommentCriteria->equals($criteria)) {
					$count = TaskCommentPeer::doCount($criteria, false, $con);
				} else {
					$count = count($this->collTaskComments);
				}
			} else {
				$count = count($this->collTaskComments);
			}
		}
		return $count;
	}

	/**
	 * Method called to associate a TaskComment object to this object
	 * through the TaskComment foreign key attribute.
	 *
	 * @param      TaskComment $l TaskComment
	 * @return     void
	 * @throws     PropelException
	 */
	public function addTaskComment(TaskComment $l)
	{
		if ($this->collTaskComments === null) {
			$this->initTaskComments();
		}
		if (!in_array($l, $this->collTaskComments, true)) { // only add it if the **same** object is not already associated
			array_push($this->collTaskComments, $l);
			$l->setTask($this);
		}
	}


